def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    feature_values = set()
    labels = set(y_train)
    for x in X_train:
        feature_values.update(x)
    feature_values = sorted(feature_values)
    labels = sorted(labels)
    weights = defaultdict(float)
    empirical_counts = defaultdict(int)
    for (x, y) in zip(X_train, y_train):
        for feature in x:
            empirical_counts[feature, y] += 1
    empirical_probs = {k: v / len(X_train) for (k, v) in empirical_counts.items()}
    for _ in range(n_iter):
        largest_update = 0
        for ((feature, label), empirical_prob) in empirical_probs.items():
            model_prob = 0
            for y in labels:
                model_prob += math.exp(sum((weights[f, y] for f in X_train if f == feature)))
            model_prob = math.exp(weights[feature, label]) / model_prob
            update = math.log(empirical_prob / model_prob)
            weights[feature, label] += update
            largest_update = max(largest_update, abs(update))
        if largest_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_prob = -float('inf')
        predicted_label = None
        for y in labels:
            prob = sum((weights[f, y] for f in x))
            if prob > max_prob:
                max_prob = prob
                predicted_label = y
        predictions.append(predicted_label)
    return predictions
