def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):

    def linear_kernel(x1, x2):
        return np.dot(x1, x2)

    def rbf_kernel(x1, x2, sigma=1.0):
        return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))
    (n_samples, n_features) = data.shape
    alphas = np.zeros(n_samples)
    bias = 0.0
    for t in range(1, iterations + 1):
        learning_rate = 1.0 / (lambda_val * t)
        for i in range(n_samples):
            if kernel == 'linear':
                kernel_value = linear_kernel(data[i], data)
            elif kernel == 'rbf':
                kernel_value = np.array([rbf_kernel(data[i], data[j], sigma) for j in range(n_samples)])
            else:
                raise ValueError("Unsupported kernel type. Choose 'linear' or 'rbf'.")
            prediction = np.sum(alphas * labels * kernel_value) + bias
            margin = labels[i] * prediction
            if margin < 1:
                alphas[i] += learning_rate * labels[i]
                bias += learning_rate * labels[i]
        alphas = np.maximum(0, 1 - learning_rate * lambda_val / (2 * n_samples)) * alphas
    alphas = alphas.round(4).tolist()
    bias = round(bias, 4)
    return (alphas, bias)
