from collections import Counter
from typing import Dict, List, Any
def entropy(values: List[Any]) -> float:
    """Calculate the entropy of a list of class values."""
    total = len(values)
    if total == 0:
        return 0.0
    counts = Counter(values)
    return -sum((count / total * math.log2(count / total) for count in counts.values()))
def information_gain(examples: List[dict], attribute: str, target_attr: str) -> float:
    """Calculate the information gain of an attribute."""
    total_entropy = entropy([example[target_attr] for example in examples])
    values = set((example[attribute] for example in examples))
    weighted_entropy = 0.0
    for value in values:
        subset = [example for example in examples if example[attribute] == value]
        subset_entropy = entropy([example[target_attr] for example in subset])
        weighted_entropy += len(subset) / len(examples) * subset_entropy
    return total_entropy - weighted_entropy
def majority_value(examples: List[dict], target_attr: str) -> Any:
    """Return the most common target value in the examples."""
    return Counter((example[target_attr] for example in examples)).most_common(1)[0][0]
def learn_decision_tree(examples: List[dict], attributes: List[str], target_attr: str) -> Dict:
    """Learn a decision tree using entropy and information gain."""
    if not examples:
        return majority_value(examples, target_attr)
    if all((example[target_attr] == examples[0][target_attr] for example in examples)):
        return examples[0][target_attr]
    if not attributes:
        return majority_value(examples, target_attr)
    best_attr = max(attributes, key=lambda a: information_gain(examples, a, target_attr))
    tree = {best_attr: {}}
    remaining_attributes = [a for a in attributes if a != best_attr]
    for value in set((example[best_attr] for example in examples)):
        subset = [example for example in examples if example[best_attr] == value]
        subtree = learn_decision_tree(subset, remaining_attributes, target_attr)
        tree[best_attr][value] = subtree
    return tree
assert learn_decision_tree([ {'Outlook': 'Sunny', 'Wind': 'Weak', 'PlayTennis': 'No'}, {'Outlook': 'Overcast', 'Wind': 'Strong', 'PlayTennis': 'Yes'}, {'Outlook': 'Rain', 'Wind': 'Weak', 'PlayTennis': 'Yes'}, {'Outlook': 'Sunny', 'Wind': 'Strong', 'PlayTennis': 'No'}, {'Outlook': 'Sunny', 'Wind': 'Weak', 'PlayTennis': 'Yes'}, {'Outlook': 'Overcast', 'Wind': 'Weak', 'PlayTennis': 'Yes'}, {'Outlook': 'Rain', 'Wind': 'Strong', 'PlayTennis': 'No'}, {'Outlook': 'Rain', 'Wind': 'Weak', 'PlayTennis': 'Yes'} ], ['Outlook', 'Wind'], 'PlayTennis') == {'Outlook': {'Sunny': {'Wind': {'Weak': 'No', 'Strong': 'No'}}, 'Rain': {'Wind': {'Weak': 'Yes', 'Strong': 'No'}}, 'Overcast': 'Yes'}}

