def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    n_samples = data.shape[0]
    alphas = np.zeros(n_samples)
    b = 0.0
    for t in range(1, iterations + 1):
        eta = 1.0 / (lambda_val * t)
        if kernel == 'linear':
            K = np.dot(data, data.T)
        elif kernel == 'rbf':
            K = np.zeros((n_samples, n_samples))
            for i in range(n_samples):
                for j in range(n_samples):
                    K[i, j] = np.exp(-np.linalg.norm(data[i] - data[j]) ** 2 / (2 * sigma ** 2))
        else:
            raise ValueError("Unsupported kernel type. Use 'linear' or 'rbf'.")
        predictions = np.dot(alphas * labels, K) + b
        subgradient = np.zeros(n_samples)
        for i in range(n_samples):
            if labels[i] * predictions[i] < 1:
                subgradient[i] = -labels[i] * K[i, :].dot(alphas * labels) - b * labels[i]
            else:
                subgradient[i] = -b * labels[i]
        alphas = (1 - eta * lambda_val) * alphas - eta * subgradient
        alphas = np.clip(alphas, 0, None)
        b = b - eta * np.mean(subgradient)
    alphas_rounded = np.round(alphas, 4).tolist()
    b_rounded = np.round(b, 4)
    return (alphas_rounded, b_rounded)
assert pegasos_kernel_svm(np.array([[1, 2], [2, 3], [3, 1], [4, 1]]), np.array([1, 1, -1, -1]), kernel='linear', lambda_val=0.01, iterations=100) == ([100.0, 0.0, -100.0, -100.0], -937.4755)
