import numpy as np
def rref(matrix):
    """
    Converts a given matrix into its Reduced Row Echelon Form (RREF).

    Parameters:
    matrix (np.ndarray): A 2D NumPy array representing the matrix.

    Returns:
    list: A Python list representation of the RREF matrix.
    """
    matrix = matrix.astype(float)
    (rows, cols) = matrix.shape
    lead = 0
    for r in range(rows):
        if lead >= cols:
            return matrix.tolist()
        i = r
        while matrix[i, lead] == 0:
            i += 1
            if i == rows:
                i = r
                lead += 1
                if lead == cols:
                    return matrix.tolist()
        matrix[[i, r]] = matrix[[r, i]]
        matrix[r] = matrix[r] / matrix[r, lead]
        for i in range(rows):
            if i != r and matrix[i, lead] != 0:
                matrix[i] = matrix[i] - matrix[i, lead] * matrix[r]
        lead += 1
    return matrix.tolist()
assert rref(np.array([ [1, 2, -1, -4], [2, 3, -1, -11], [-2, 0, -3, 22] ])) == [[ 1., 0., 0., -8.], [ 0., 1., 0., 1.], [-0., -0., 1., -2.]]
assert rref(np.array([ [2, 4, -2], [4, 9, -3], [-2, -3, 7] ])) == [[ 1., 0., 0.], [ 0., 1., 0.], [ 0., 0., 1.]]
