def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('The corpus is empty. Cannot compute TF-IDF.')
    vocabulary = set()
    for doc in corpus:
        vocabulary.update(doc)
    vocabulary.update(query)
    vocabulary = list(vocabulary)
    tf_matrix = []
    for doc in corpus:
        tf = {}
        doc_len = len(doc)
        for word in doc:
            tf[word] = tf.get(word, 0) + 1 / doc_len
        tf_matrix.append(tf)
    num_docs = len(corpus)
    idf = {}
    for word in vocabulary:
        df = sum((1 for doc in corpus if word in doc))
        idf[word] = np.log((num_docs + 1) / (df + 1))
    tf_idf_scores = []
    for doc_tf in tf_matrix:
        doc_scores = []
        for word in query:
            tf = doc_tf.get(word, 0)
            idf_score = idf.get(word, 0)
            doc_scores.append(round(tf * idf_score, 5))
        tf_idf_scores.append(doc_scores)
    return np.array(tf_idf_scores).tolist()
