def calculate_brightness(img):
    if not img:
        return -1
    num_rows = len(img)
    if num_rows == 0:
        return -1
    total_pixels = 0
    total_brightness = 0
    expected_length = len(img[0])
    for row in img:
        if len(row) != expected_length:
            return -1
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
            total_brightness += pixel
            total_pixels += 1
    average = total_brightness / total_pixels
    return round(average, 2)
