def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    if x > 50:
        return round(x, 4)
    elif x < -50:
        return round(0.0, 4)
    else:
        return round(math.log(1 + math.exp(x)), 4)
