def rref(matrix):
    matrix = np.array(matrix, dtype=float)
    (r, c) = matrix.shape
    (i, j) = (0, 0)
    while i < r and j < c:
        k = np.argmax(np.abs(matrix[i:, j])) + i
        pivot = matrix[k, j]
        if np.abs(pivot) < 1e-14:
            j += 1
        else:
            matrix[[i, k]] = matrix[[k, i]]
            matrix[i] = matrix[i] / pivot
            for k in range(r):
                if k != i:
                    matrix[k] = matrix[k] - matrix[i] * matrix[k, j]
            i += 1
            j += 1
    return matrix.tolist()

