    idf = {}
    for term in query:
        df = sum([1 for doc in corpus if term in doc])
        idf[term] = math.log((len(corpus) - df + 0.5) / (df + 0.5))
    scores = []
    for doc in corpus:
        score = 0
        for term in query:
            if term in doc:
                tf = doc.count(term)
                qtf = query.count(term)
                score += idf[term] * (tf * (k1 + 1) / (tf + k1 * (1 - b + b * (len(doc) / avgdl)))) * ((k1 + 1) * qtf / (k1 + qtf))
        scores.append(round(score, 3))
    return scores

import math
def elu(x: float, alpha: float=1.0) -> float:
    """
    Compute the ELU activation function.

    Args:
        x (float): Input value
        alpha (float): ELU parameter for negative values (default: 1.0)

    Returns:
        float: ELU activation value
    """
    if x >= 0:
        return round(x, 4)
    else:
        return round(alpha * (math.exp(x) - 1), 4)

import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    try:
        result = math.log1p(math.exp(x))
        return round(result, 4)
    except OverflowError:
        return float('inf')
    except (ValueError, TypeError):
        return float('nan')

import numpy as np
def global_avg_pool(x: np.ndarray):
    if len(x.shape) != 3:
        raise ValueError('Input should be a 3D numpy array')
    avg_pool = np.mean(x, axis=(0, 1))
    return avg_pool

def poly_term_derivative(c: float, x: float, n: float) -> float:
    derivative = c * n * x ** (n - 1)
    return round(derivative, 4)

import numpy as np
def cramers_rule(A, b):
    if np.linalg.det(A) == 0:
        return -1
    n = A.shape[0]
    x = np.zeros(n)
    for i in range(n):
        A_i = A.copy()
        A_i[:, i] = b
        det_A_i = np.linalg.det(A_i)
        x[i] = det_A_i / np.linalg.det(A)
    x = np.round(x, 4)
    return x.tolist()

import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or len(p) == 0 or len(q) == 0:
        return 0.0
    bc_distance = sum([np.sqrt(p_i * q_i) for (p_i, q_i) in zip(p, q)])
    bc_distance = -np.log(bc_distance)
    return round(bc_distance, 4)

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    else:
        return [a[i] + b[i] for i in range(len(a))]

import numpy as np
def moe(x: np.ndarray, We: np.ndarray, Wg: np.ndarray, n_experts: int, top_k: int):
    gating_scores = np.dot(x, Wg.T)
    gating_probabilities = np.exp(gating_scores) / np.sum(np.exp(gating_scores), axis=1, keepdims=True)
    top_k_indices = np.argpartition(gating_probabilities, -top_k, axis=1)[:, -top_k:]
    output = np.zeros_like(x)
    for i in range(x.shape[0]):
        for j in range(top_k):
            expert_index = top_k_indices[i, j]
            output[i] += gating_probabilities[i, expert_index] * np.dot(x[i], We[expert_index])
    output = np.round(output, 4)
    return output.tolist()

import numpy as np
def group_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, num_groups: int, epsilon: float=1e-05):
    (B, C, H, W) = X.shape
    assert C % num_groups == 0, 'Number of channels should be divisible by number of groups'
    X_reshaped = X.reshape(B, num_groups, C // num_groups, H, W)
    mean = np.mean(X_reshaped, axis=(2, 3, 4), keepdims=True)
    var = np.var(X_reshaped, axis=(2, 3, 4), keepdims=True)
    X_normalized = (X_reshaped - mean) / np.sqrt(var + epsilon)
    Y = gamma * X_normalized + beta
    return Y.round(4).tolist()

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for _ in range(max_iters):
        grad = df(x)
        if np.abs(grad) < tolerance:
            break
        x -= learning_rate * grad
    return round(x, 4)
