def svd_2x2_singular_values(A: np.ndarray) -> tuple:

    def jacobi_method(A, max_iter=1000, tol=1e-08):

        def max_elem(A):
            n = A.shape[0]
            a_max = 0.0
            for i in range(n - 1):
                for j in range(i + 1, n):
                    if abs(A[i, j]) >= a_max:
                        a_max = abs(A[i, j])
                        (p, q) = (i, j)
            return (a_max, p, q)

        def rotate(A, p, q):
            n = A.shape[0]
            cs = np.cos(np.arctan(2 * A[p, q] / (A[q, q] - A[p, p])) / 2)
            sn = np.sin(np.arctan(2 * A[p, q] / (A[q, q] - A[p, p])) / 2)
            B = np.identity(n)
            B[p, p] = cs
            B[q, q] = cs
            B[p, q] = -sn
            B[q, p] = sn
            return B
        n = A.shape[0]
        B = np.identity(n)
        for _ in range(max_iter):
            (a_max, p, q) = max_elem(A)
            if a_max < tol:
                return (np.diag(A), B)
            R = rotate(A, p, q)
            B = np.dot(B, R)
            A = np.dot(np.dot(R.T, A), R)
        print('Jacobi method did not converge')
        return (np.diag(A), B)
    (eigvals, V) = jacobi_method(np.dot(A.T, A))
    eigvals = np.sqrt(np.abs(eigvals))
    sort_indices = np.argsort(eigvals)[::-1]
    eigvals = eigvals[sort_indices]
    V = V[:, sort_indices]
    U = np.dot(A, V)
    for i in range(U.shape[1]):
        U[:, i] /= eigvals[i]
    return (np.round(U, 4), np.round(eigvals, 4), np.round(V.T, 4))
