def gain(data, attr, target_attr):
    total = len(data)
    values = set(data[attr])
    gain = entropy(data[target_attr])
    for val in values:
        subset = data[data[attr] == val]
        subset_entropy = entropy(subset[target_attr])
        gain -= len(subset) / total * subset_entropy
    return gain
