def linear_kernel(x, y):
    return np.dot(x, y)
def rbf_kernel(x, y, sigma=1.0):
    return np.exp(-np.linalg.norm(x - y) ** 2 / (2 * sigma ** 2))
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (num_samples, num_features) = data.shape
    alpha = np.zeros(num_samples)
    bias = 0
    kernel_function = linear_kernel if kernel == 'linear' else lambda x, y: rbf_kernel(x, y, sigma)
    for _ in range(iterations):
        for i in range(num_samples):
            result = sum([alpha[j] * labels[j] * kernel_function(data[j], data[i]) for j in range(num_samples)])
            result += bias
            if labels[i] * result < 1:
                alpha[i] += 1
                bias += lambda_val * labels[i]
    return (alpha.tolist(), round(bias, 4))
