import numpy as np
def sigmoid(x):
    return 1 / (1 + np.exp(-x))
def sigmoid_derivative(x):
    return x * (1 - x)
def train_neuron(features: np.ndarray, labels: np.ndarray, initial_weights: np.ndarray, initial_bias: float, learning_rate: float, epochs: int) -> (np.ndarray, float, list[float]):
    weights = initial_weights
    bias = initial_bias
    mse_values = []
    for _ in range(epochs):
        weighted_sum = np.dot(features, weights) + bias
        predictions = sigmoid(weighted_sum)
        mse = np.mean((labels - predictions) ** 2)
        mse_values.append(round(mse, 4))
        error = predictions - labels
        d_predictions = 2 * error * sigmoid_derivative(predictions)
        weights -= learning_rate * np.dot(features.T, d_predictions)
        bias -= learning_rate * np.sum(d_predictions)
    return (weights.tolist(), round(bias, 4), mse_values)
