def calculate_brightness(img):
    if not img:
        return -1
    row_length = len(img[0])
    for row in img:
        if len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
    total_brightness = 0
    total_pixels = 0
    for row in img:
        total_brightness += sum(row)
        total_pixels += len(row)
    average_brightness = total_brightness / total_pixels
    average_brightness = round(average_brightness, 2)
    return average_brightness
