import math
def calculate_bm25_scores(corpus, query, k1=1.5, b=0.75):
    avgdl = sum([len(doc) for doc in corpus]) / len(corpus)
    N = len(corpus)
    idf = {}
    for doc in corpus:
        for word in set(doc):
            if word not in idf:
                idf[word] = math.log((N - sum([1 for doc in corpus if word in doc]) + 0.5) / (sum([1 for doc in corpus if word in doc]) + 0.5))
    scores = []
    for doc in corpus:
        score = 0
        for word in query:
            if word in doc:
                tf = doc.count(word)
                qf = query.count(word)
                score += idf[word] * ((k1 + 1) * tf) / (k1 * (1 - b + b * (len(doc) / avgdl)) + tf) * ((k1 + 1) * qf) / (k1 + qf)
        scores.append(round(score, 3))
    return scores
