        The softplus value: log(1 + e^x)
    """
    try:
        result = math.log1p(math.exp(x))
        return round(result, 4)
    except OverflowError:
        return float('inf')
    except ValueError:
        return float('-inf')
assert softplus(0) == 0.6931
assert softplus(100) == 100.0
assert softplus(-100) == 0.0
assert softplus(2) == 2.1269
assert softplus(-2) == 0.1269

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    else:
        return [a[i] + b[i] for i in range(len(a))]
assert vector_sum([1, 2, 3], [4, 5, 6]) == [5, 7, 9]
assert vector_sum([1, 2], [1, 2, 3]) == -1
assert vector_sum([1.5, 2.5, 3.0], [2, 1, 4]) == [3.5, 3.5, 7.0]

