                idf[word] = math.log((N - sum([1 for doc in corpus if word in doc]) + 0.5) / (sum([1 for doc in corpus if word in doc]) + 0.5))
    scores = []
    for doc in corpus:
        score = 0
        for word in query:
            if word in doc:
                tf = doc.count(word)
                qf = query.count(word)
                score += idf[word] * ((k1 + 1) * tf) / (k1 * (1 - b + b * (len(doc) / avgdl)) + tf) * ((k1 + 1) * qf) / (k1 + qf)
        scores.append(round(score, 3))
    return scores

import math
def elu(x: float, alpha: float=1.0) -> float:
    """
    Compute the ELU activation function.

    Args:
        x (float): Input value
        alpha (float): ELU parameter for negative values (default: 1.0)

    Returns:
        float: ELU activation value
    """
    if x >= 0:
        return round(x, 4)
    else:
        return round(alpha * (math.exp(x) - 1), 4)

import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    try:
        result = math.log1p(math.exp(x))
        return round(result, 4)
    except OverflowError:
        return float('inf')
    except ValueError:
        return float('-inf')

