import numpy as np
def orthonormal_basis(vectors: list[list[float]], tol: float=1e-10):
    basis = []
    for v in vectors:
        w = np.array(v, dtype=float)
        for vi in basis:
            vi = np.array(vi)
            w = w - np.dot(w, vi) * vi
        if np.linalg.norm(w) > tol:
            basis.append(list(w / np.linalg.norm(w)))
    return [np.round(np.array(v), 4).tolist() for v in basis]

import numpy as np
def cramers_rule(A, b):
    if np.linalg.det(A) == 0:
        return -1
    n = A.shape[0]
    x = np.zeros(n)
    for i in range(n):
        A_i = A.copy()
        A_i[:, i] = b
        det_A_i = np.linalg.det(A_i)
        x[i] = det_A_i / np.linalg.det(A)
    x = np.round(x, 4)
    return x.tolist()

import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or len(p) == 0 or len(q) == 0:
        return 0.0
    bc_distance = sum([np.sqrt(p_i * q_i) for (p_i, q_i) in zip(p, q)])
    bc_distance = -np.log(bc_distance)
    return round(bc_distance, 4)

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    else:
        return [a[i] + b[i] for i in range(len(a))]

import numpy as np
def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):
    policy_gradients = []
    for episode in episodes:
        returns = []
        G = 0
        for t in reversed(range(len(episode))):
            (state, action, reward) = episode[t]
            G = reward + G
            returns.append(G)
        returns = returns[::-1]
        for t in range(len(episode)):
            (state, action, reward) = episode[t]
            gradient = compute_policy_gradient_at_step(theta, state, action, returns[t])
            policy_gradients.append(gradient)
    avg_policy_gradient = np.mean(policy_gradients, axis=0)
    return np.round(avg_policy_gradient, 4).tolist()
def compute_policy_gradient_at_step(theta: np.ndarray, state: int, action: int, G: float):
    policy = compute_policy(theta, state)
    gradient = policy / policy[action] * G
    return np.round(gradient, 4)
def compute_policy(theta: np.ndarray, state: int):
    policy = np.exp(theta[state, :]) / np.sum(np.exp(theta[state, :]))
    return policy

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for _ in range(max_iters):
        grad = df(x)
        if np.abs(grad) < tolerance:
            break
        x -= learning_rate * grad
    return round(x, 4)
