        A_copy[:, i] = b
        det_A_copy = np.linalg.det(A_copy)
        x[i] = det_A_copy / np.linalg.det(A)
    x = np.round(x, 4)
    return x.tolist()

import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or len(p) == 0 or len(q) == 0:
        return 0.0
    bc_distance = sum([np.sqrt(p_i * q_i) for (p_i, q_i) in zip(p, q)])
    return round(1 - bc_distance, 4)

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    else:
        return [a[i] + b[i] for i in range(len(a))]

import numpy as np
def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):
    (num_states, num_actions) = theta.shape
    policy_gradient = np.zeros_like(theta)
    for episode in episodes:
        T = len(episode)
        G = 0
        for t in range(T - 1, -1, -1):
            (s, a, r) = episode[t]
            G += r
            theta_s = theta[s, :]
            policy_s = np.exp(theta_s) / np.exp(theta_s).sum()
            for a_ in range(num_actions):
                if a_ == a:
                    policy_gradient[s, a_] += (1 - policy_s[a_]) * G
                else:
                    policy_gradient[s, a_] -= policy_s[a_] * G
    policy_gradient /= len(episodes)
    return policy_gradient.round(4).tolist()

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:
    x = start_x
    for i in range(max_iters):
        gradient = 4 * x ** 3 - 9 * x ** 2
        if np.abs(gradient) < tolerance:
            break
        x = x - learning_rate * gradient
    return round(x, 4)
