from collections import Counter
def learn_decision_tree(examples, attributes, target_attr):

    def entropy(examples):
        count = Counter([e[target_attr] for e in examples])
        probs = [float(v) / len(examples) for v in count.values()]
        return -sum([p * math.log(p, 2) for p in probs])

    def information_gain(examples, attr):
        attr_values = set([e[attr] for e in examples])
        weighted_entropy = sum([len([e for e in examples if e[attr] == val]) / len(examples) * entropy([e for e in examples if e[attr] == val]) for val in attr_values])
        return entropy(examples) - weighted_entropy

    def plurality_value(examples):
        count = Counter([e[target_attr] for e in examples])
        return max(count.items(), key=lambda x: x[1])[0]

    def same_classification(examples):
        return len(set([e[target_attr] for e in examples])) == 1
    if not examples:
        return {}
    elif same_classification(examples):
        return {target_attr: examples[0][target_attr]}
    elif not attributes:
        return {target_attr: plurality_value(examples)}
    else:
        best_attr = max(attributes, key=lambda attr: information_gain(examples, attr))
        tree = {best_attr: {}}
        remaining_attrs = [attr for attr in attributes if attr != best_attr]
        for val in set([e[best_attr] for e in examples]):
            exs = [e for e in examples if e[best_attr] == val]
            subtree = learn_decision_tree(exs, remaining_attrs, target_attr)
            tree[best_attr][val] = subtree
        return tree

