            elif kernel == 'rbf':
                kernel_output = rbf_kernel(data[i], data, sigma)
            else:
                raise ValueError("Invalid kernel type. Choose from 'linear' or 'rbf'.")
            prediction = np.dot(alpha * labels, kernel_output) + bias
            if labels[i] * prediction < 1:
                alpha[i] += 1
                bias += labels[i]
        alpha[alpha > lambda_val] -= lambda_val
        alpha[alpha < -lambda_val] += lambda_val
    support_vectors = alpha > 1e-05
