def gradient_descent(X, y, weights, learning_rate, n_iterations, batch_size=1, method='batch'):
    X = np.concatenate((np.ones((X.shape[0], 1)), X), axis=1)
    weights = np.array(weights)

    def loss_function(X, y, weights):
        return np.mean((np.dot(X, weights) - y) ** 2)

    def gradient(X, y, weights):
        return 2 * np.dot(X.T, np.dot(X, weights) - y) / X.shape[0]
    for i in range(n_iterations):
        if method == 'batch':
            weights = weights - learning_rate * gradient(X, y, weights)
        elif method == 'stochastic':
            for j in range(X.shape[0]):
                weights = weights - learning_rate * gradient(X[j, :].reshape(1, -1), y[j], weights)
        elif method == 'mini-batch':
            for j in range(0, X.shape[0], batch_size):
                X_batch = X[j:j + batch_size, :]
                y_batch = y[j:j + batch_size]
                weights = weights - learning_rate * gradient(X_batch, y_batch, weights)
        else:
            raise ValueError("Invalid method. Choose from 'batch', 'stochastic', or 'mini-batch'.")
    return weights.tolist()

