def gaussian_elimination(A, b):
    augmented_matrix = np.concatenate((A, b.reshape(-1, 1)), axis=1)
    n = len(b)
    for i in range(n):
        max_row = i
        for j in range(i + 1, n):
            if np.abs(augmented_matrix[j, i]) > np.abs(augmented_matrix[max_row, i]):
                max_row = j
        augmented_matrix[[i, max_row]] = augmented_matrix[[max_row, i]]
        for j in range(i + 1, n):
            factor = augmented_matrix[j, i] / augmented_matrix[i, i]
            augmented_matrix[j] -= factor * augmented_matrix[i]
    x = np.zeros(n)
    for i in range(n - 1, -1, -1):
        x[i] = (augmented_matrix[i, -1] - np.dot(augmented_matrix[i, i + 1:-1], x[i + 1:])) / augmented_matrix[i, i]
    return np.round(x.tolist(), 4)

