import math
def calculate_bm25_scores(corpus, query, k1=1.5, b=0.75):
    avgdl = sum([len(doc) for doc in corpus]) / len(corpus)
    N = len(corpus)
    idf = {}
    for doc in corpus:
        for word in set(doc):
            if word not in idf:
                idf[word] = math.log((N - sum([1 for i in corpus if word in i]) + 0.5) / (sum([1 for i in corpus if word in i]) + 0.5))
    scores = []
    for doc in corpus:
        score = 0
        for word in query:
            if word in doc:
                tf = doc.count(word)
                qf = query.count(word)
                score += idf[word] * ((k1 + 1) * tf) / (k1 * (1 - b + b * (len(doc) / avgdl)) + tf) * ((k1 + 1) * qf) / (k1 + qf)
        scores.append(round(score, 3))
    return scores

import numpy as np
def multi_head_attention(X: np.ndarray, W_q: np.ndarray, W_k: np.ndarray, W_v: np.ndarray, n_heads: int) -> list:

    def compute_qkv(X, W_q, W_k, W_v):
        return (np.dot(X, W_q), np.dot(X, W_k), np.dot(X, W_v))

    def self_attention(Q, K, V):
        d_k = K.shape[1]
        attn_scores = np.dot(Q, K.T) / np.sqrt(d_k)
        attn_weights = softmax(attn_scores, axis=-1)
        output = np.dot(attn_weights, V)
        return output

    def softmax(x, axis=None):
        return np.exp(x - np.max(x)) / np.sum(np.exp(x - np.max(x)), axis=axis, keepdims=True)
    (X_q, X_k, X_v) = compute_qkv(X, W_q, W_k, W_v)
    X_q = np.split(X_q, n_heads, axis=-1)
    X_k = np.split(X_k, n_heads, axis=-1)
    X_v = np.split(X_v, n_heads, axis=-1)
    output = []
    for i in range(n_heads):
        output.append(self_attention(X_q[i], X_k[i], X_v[i]))
    return np.concatenate(output, axis=-1).round(4).tolist()

