import numpy as np
def orthonormal_basis(vectors: list[list[float]], tol: float=1e-10):
    basis = []
    for v in vectors:
        w = np.array(v, dtype=float)
        for vi in basis:
            vi = np.array(vi)
            w = w - np.dot(w, vi) * vi
        if np.linalg.norm(w) > tol:
            basis.append(list(w / np.linalg.norm(w)))
    return [np.round(np.array(v), 4).tolist() for v in basis]

import numpy as np
def cramers_rule(A, b):
    if np.linalg.det(A) == 0:
        return -1
    n = len(b)
    x = np.zeros(n)
    for i in range(n):
        Ai = np.copy(A)
        Ai[:, i] = b
        det_Ai = np.linalg.det(Ai)
        x[i] = det_Ai / np.linalg.det(A)
    x = np.round(x, 4)
    return x.tolist()

