def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q):
        return 0.0
    if len(p) == 0 or len(q) == 0:
        return 0.0
    bc_distance = -np.log(np.sum([np.sqrt(p_i * q_i) for (p_i, q_i) in zip(p, q)]))
    bc_distance = round(bc_distance, 4)
    return bc_distance

