def softmax(x):
    """Compute softmax values for each sets of scores in x."""
    e_x = np.exp(x - np.max(x))
    return e_x / e_x.sum()
def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):
    (num_states, num_actions) = theta.shape
    policy_gradient = np.zeros_like(theta)
    for episode in episodes:
        T = len(episode)
        G = 0
        for t in reversed(range(T)):
            (state, action, reward) = episode[t]
            probs = softmax(theta[state, :])
            G = G + reward
            for a in range(num_actions):
                if a == action:
                    policy_gradient[state, a] += (G - theta[state, a]) * (1 - probs[a])
                else:
                    policy_gradient[state, a] -= theta[state, a] * probs[a]
    policy_gradient /= len(episodes)
    return policy_gradient.round(4).tolist()

