def dynamic_tanh(x: np.ndarray, alpha: float, gamma: float, beta: float) -> list[float]:
    mean = np.mean(x)
    std = np.std(x)
    dyt = alpha * np.tanh(beta * ((x - mean) / std)) + gamma
    return np.round(dyt, 4).tolist()
