from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:

    def entropy(examples):
        count = Counter([example[target_attr] for example in examples])
        total = sum(count.values())
        return -sum((count[i] / total * math.log2(count[i] / total) for i in count))

    def gain(examples, attr):
        count = Counter([example[attr] for example in examples])
        total = sum(count.values())
        return entropy(examples) - sum((count[i] / total * entropy([example for example in examples if example[attr] == i]) for i in count))

    def plurality_value(examples):
        count = Counter([example[target_attr] for example in examples])
        return max(count, key=count.get)

    def same_classification(examples):
        classifications = [example[target_attr] for example in examples]
        return len(set(classifications)) == 1
    if not examples:
        return {}
    elif same_classification(examples):
        return {target_attr: examples[0][target_attr]}
    elif not attributes:
        return {target_attr: plurality_value(examples)}
    else:
        best_attr = max(attributes, key=lambda attr: gain(examples, attr))
        tree = {best_attr: {}}
        remaining_attr = [attr for attr in attributes if attr != best_attr]
        for value in set([example[best_attr] for example in examples]):
            subset = [example for example in examples if example[best_attr] == value]
            subtree = learn_decision_tree(subset, remaining_attr, target_attr)
            tree[best_attr][value] = subtree
        return tree

