            grad_w = np.dot(self.layer_input.T, accum_grad)
            grad_w0 = np.sum(accum_grad, axis=0, keepdims=True)
            self.W = self.W - grad_w
            self.w0 = self.w0 - grad_w0
        accum_grad = np.dot(accum_grad, W.T)
        return accum_grad

