def rref(matrix):
    matrix = np.array(matrix, dtype=np.float64)
    (r, c) = matrix.shape
    (i, j) = (0, 0)
    while i < r and j < c:
        k = np.argmax(np.abs(matrix[i:, j])) + i
        temp = np.copy(matrix[i, :])
        matrix[i, :] = matrix[k, :]
        matrix[k, :] = temp
        if matrix[i, j] == 0:
            j += 1
            continue
        matrix[i, :] = matrix[i, :] / matrix[i, j]
        for k in range(r):
            if k != i:
                matrix[k, :] = matrix[k, :] - matrix[i, :] * matrix[k, j]
        i += 1
        j += 1
    return matrix.tolist()

