    pos_embeddings = np.take(params['wpe'], range(len(tokens)), axis=0)
    x = token_embeddings + pos_embeddings
    for _ in range(hparams['n_head']):
        attn = np.dot(x, x.T)
        attn /= np.sqrt(hparams['n_head'])
        attn = np.softmax(attn, axis=-1)
