def calculate_bm25_scores(corpus, query, k1=1.5, b=0.75):

    def idf(word):
        df = sum([1 for doc in corpus if word in doc])
        return math.log((len(corpus) - df + 0.5) / (df + 0.5))

    def score(doc, word):
        word_count = doc.count(word)
        return idf(word) * (word_count * (k1 + 1)) / (word_count + k1 * (1 - b + b * len(doc) / avgdl))
    avgdl = sum([len(doc) for doc in corpus]) / len(corpus)
