        total_score = sum([score(doc, word) for word in query if word in doc])
        scores.append(round(total_score, 3))
    return scores

import numpy as np
def multi_head_attention(X: np.ndarray, W_q: np.ndarray, W_k: np.ndarray, W_v: np.ndarray, n_heads: int) -> list:

    def compute_qkv(X, W_q, W_k, W_v):
        return (np.dot(X, W_q), np.dot(X, W_k), np.dot(X, W_v))

    def self_attention(Q, K, V):
        d_k = K.shape[1]
        attn_scores = np.dot(Q, K.T) / np.sqrt(d_k)
        attn_weights = np.round(softmax(attn_scores, axis=-1), 4)
        output = np.dot(attn_weights, V)
        return output

    def softmax(x, axis=None):
        return np.round(np.exp(x - np.max(x)) / np.sum(np.exp(x - np.max(x)), axis=axis, keepdims=True), 4)
    d_model = W_q.shape[0]
    d_k = d_model // n_heads
    (Q, K, V) = compute_qkv(X, W_q, W_k, W_v)
    Q_heads = np.split(Q, n_heads, axis=-1)
    K_heads = np.split(K, n_heads, axis=-1)
    V_heads = np.split(V, n_heads, axis=-1)
    outputs = []
    for i in range(n_heads):
        output = self_attention(Q_heads[i], K_heads[i], V_heads[i])
        outputs.append(output)
    output = np.concatenate(outputs, axis=-1)
    return output.tolist()

import math
def elu(x: float, alpha: float=1.0) -> float:
    """
    Compute the ELU activation function.

    Args:
        x (float): Input value
        alpha (float): ELU parameter for negative values (default: 1.0)

    Returns:
        float: ELU activation value
    """
    if x >= 0:
        return round(x, 4)
    else:
        return round(alpha * (math.exp(x) - 1), 4)

import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    if x > 500:
        return round(x, 4)
    elif x < -500:
        return 0.0
    else:
        return round(math.log(1 + math.exp(x)), 4)

import numpy as np
def global_avg_pool(x: np.ndarray):
    if len(x.shape) != 3:
        raise ValueError('Input should be a 3D array')
    avg_pool = np.mean(x, axis=(0, 1))
    return avg_pool

import numpy as np
def batch_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, epsilon: float=1e-05):
    mean = np.mean(X, axis=(0, 2, 3), keepdims=True)
    var = np.var(X, axis=(0, 2, 3), keepdims=True)
    X_hat = (X - mean) / np.sqrt(var + epsilon)
    Y = gamma * X_hat + beta
    return Y.round(4).tolist()

def poly_term_derivative(c: float, x: float, n: float) -> float:
    derivative = c * n * x ** (n - 1)
    return round(derivative, 4)

import numpy as np
def orthonormal_basis(vectors: list[list[float]], tol: float=1e-10):
    basis = []
    for v in vectors:
        w = np.array(v, dtype=float)
        for vi in basis:
            vi = np.array(vi)
            w = w - np.dot(w, vi) * vi
        if np.linalg.norm(w) > tol:
            basis.append(list(w / np.linalg.norm(w)))
    return basis

import numpy as np
def cramers_rule(A, b):
    if np.linalg.det(A) == 0:
        return -1
    n = A.shape[0]
    x = np.zeros(n)
    det_A = np.linalg.det(A)
    for i in range(n):
        A_i = np.copy(A)
        A_i[:, i] = b
        det_A_i = np.linalg.det(A_i)
        x[i] = det_A_i / det_A
    return np.round(x, 4).tolist()

import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q) or len(p) == 0 or len(q) == 0:
        return 0.0
    bc_distance = sum([np.sqrt(p_i * q_i) for (p_i, q_i) in zip(p, q)])
    return round(np.log(bc_distance), 4) if bc_distance != 0 else 0.0

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    else:
        return [x + y for (x, y) in zip(a, b)]

import numpy as np
def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):

    def softmax(x):
        e_x = np.exp(x - np.max(x))
        return e_x / e_x.sum()

    def compute_gradient(state, action, theta):
        prob = softmax(theta[state, :])
        s = prob.reshape(-1, 1)
        return np.diagflat(s) - np.dot(s, s.T)
    grad_log_pi = []
    for episode in episodes:
        T = len(episode)
        G = 0
        for t in range(T - 1, -1, -1):
            G += episode[t][2]
            (state, action, _) = episode[t]
            grad_log_pi.append(G * compute_gradient(state, action, theta))
    avg_grad_log_pi = np.sum(grad_log_pi, axis=0) / len(episodes)
    return np.round(avg_grad_pi, 4).tolist()

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for _ in range(max_iters):
        gradient = df(x)
        if abs(gradient) < tolerance:
            break
        x -= learning_rate * gradient
    return round(x, 4)
