def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float]:
    if len(a[0]) != len(b):
        return -1
    result = []
    for i in range(len(a)):
        dot_product = 0
        for j in range(len(a[i])):
            dot_product += a[i][j] * b[j]
        result.append(dot_product)
    return result
