def transpose_matrix(a: list[list[int | float]]) -> list[list[int | float]]:
    if len(a) == 0:
        return []
    rows = len(a)
    cols = len(a[0])
    transposed = [[0 for _ in range(rows)] for _ in range(cols)]
    for i in range(rows):
        for j in range(cols):
            transposed[j][i] = a[i][j]
    return transposed
