def determinant_4x4(matrix: list[list[int | float]]) -> float:
    if len(matrix) != len(matrix[0]):
        raise ValueError('The matrix is not square')
    if len(matrix) != 4:
        raise ValueError('The matrix is not 4x4')
    determinant = 0
    for j in range(4):
        determinant += (-1) ** j * matrix[0][j] * determinant_3x3([row[:j] + row[j + 1:] for row in matrix[1:]])
    return determinant
def determinant_3x3(matrix: list[list[int | float]]) -> float:
    if len(matrix) != len(matrix[0]):
        raise ValueError('The matrix is not square')
    if len(matrix) != 3:
        raise ValueError('The matrix is not 3x3')
    if len(matrix) == 2:
        return matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0]
    determinant = 0
    for j in range(3):
        determinant += (-1) ** j * matrix[0][j] * determinant_3x3([row[:j] + row[j + 1:] for row in matrix[1:]])
