def entropy(probs):
    return sum((-prob * math.log2(prob) for prob in probs))
def info_gain(examples, attr, target_attr):
    attr_values = set([ex[attr] for ex in examples])
    target_values = set([ex[target_attr] for ex in examples])
    total_examples = len(examples)
    weighted_entropy = sum((len([ex for ex in examples if ex[attr] == val]) / total_examples * entropy([len([ex for ex in examples if ex[target_attr] == target and ex[attr] == val]) / len([ex for ex in examples if ex[attr] == val]) for target in target_values]) for val in attr_values))
    return entropy([len([ex for ex in examples if ex[target_attr] == target]) / total_examples for target in target_values]) - weighted_entropy
def majority_value(examples, target_attr):
    data = [ex[target_attr] for ex in examples]
    return Counter(data).most_common(1)[0][0]
def learn_decision_tree(examples, attributes, target_attr):
    target_values = [ex[target_attr] for ex in examples]
    if not examples or len(attributes) <= 1 or len(set(target_values)) <= 1:
        return majority_value(examples, target_attr)
    else:
        gains = [(attr, info_gain(examples, attr, target_attr)) for attr in attributes]
        max_gain_attr = max(gains, key=lambda x: x[1])[0]
        tree = {max_gain_attr: {}}
        remaining_attributes = [attr for attr in attributes if attr != max_gain_attr]
        for val in set([ex[max_gain_attr] for ex in examples]):
            subtree = learn_decision_tree([ex for ex in examples if ex[max_gain_attr] == val], remaining_attributes, target_attr)
            tree[max_gain_attr][val] = subtree
        return tree
