import numpy as np
def linear_kernel(x, y):
    return np.dot(x, y)
def rbf_kernel(x, y, sigma):
    return np.exp(-np.linalg.norm(x - y) ** 2 / (2 * sigma ** 2))
def compute_kernel_matrix(data, kernel, sigma):
    kernel_matrix = np.zeros((data.shape[0], data.shape[0]))
    for i in range(data.shape[0]):
        for j in range(data.shape[0]):
            if kernel == 'linear':
                kernel_matrix[i, j] = linear_kernel(data[i], data[j])
            elif kernel == 'rbf':
                kernel_matrix[i, j] = rbf_kernel(data[i], data[j], sigma)
    return kernel_matrix
def pegasos_kernel_svm(data, labels, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    kernel_matrix = compute_kernel_matrix(data, kernel, sigma)
    (num_samples, num_features) = data.shape
    alpha = np.zeros(num_samples)
    bias = 0
    for _ in range(iterations):
        for i in range(num_samples):
            result = labels[i] * (np.dot(alpha * labels, kernel_matrix[i]) + bias)
            if result < 1:
                alpha[i] += 1
                bias += labels[i]
            alpha[i] = min(1, alpha[i])
    return (alpha.tolist(), round(bias, 4))
