def gauss_seidel(A, b, n, x_ini=None):
    L = np.tril(A)
    U = np.triu(A, 1)
    if x_ini is None:
        x_ini = np.zeros_like(b)
    for _ in range(n):
        x_ini = np.dot(np.linalg.inv(L), b - np.dot(U, x_ini))
    return np.around(x_ini.tolist(), 4)
