from collections import Counter
def compute_tf_idf(corpus, query):
    if len(corpus) == 0:
        return []
    term_frequencies = [Counter(doc) for doc in corpus]
    document_frequencies = Counter()
    for doc in corpus:
        document_frequencies.update(set(doc))
    idf = {}
    for term in document_frequencies:
        idf[term] = np.log((1 + len(corpus)) / (1 + document_frequencies[term])) + 1
    tf_idf = []
    for doc in term_frequencies:
        scores = []
        for term in query:
            tf = doc[term] / sum(doc.values())
            scores.append(round(tf * idf[term], 5))
        tf_idf.append(scores)
    return np.array(tf_idf).tolist()
