def rmse(y_true, y_pred):
    if not isinstance(y_true, (np.ndarray, list)) or not isinstance(y_pred, (np.ndarray, list)):
        raise TypeError('Invalid input type. Expected numpy array or list.')
    y_true = np.array(y_true)
    y_pred = np.array(y_pred)
    if y_true.shape != y_pred.shape:
        raise ValueError('Mismatched array shapes. The shapes of y_true and y_pred should be the same.')
    if len(y_true) == 0 or len(y_pred) == 0:
        raise ValueError('Empty arrays. Both y_true and y_pred should not be empty.')
    rmse = np.sqrt(np.mean((y_true - y_pred) ** 2))
    return round(rmse, 3)
