def inverse_2x2(matrix: list[list[float]]) -> list[list[float]]:
    det = matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0]
    if det == 0:
        return None
    else:
        inv_det = 1 / det
        inverted_matrix = [[inv_det * matrix[1][1], -inv_det * matrix[0][1]], [-inv_det * matrix[1][0], inv_det * matrix[0][0]]]
        return inverted_matrix
