from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    target_values = [example[target_attr] for example in examples]
    target_entropy = entropy(target_values)
    if len(attributes) == 0:
        return most_common(target_values)
    if target_entropy == 0:
        return examples[0][target_attr]
    best_attr = max(attributes, key=lambda attr: information_gain(examples, attr, target_attr))
    tree = {best_attr: {}}
    remaining_attributes = [attr for attr in attributes if attr != best_attr]
    for (value, subset) in split_examples(examples, best_attr):
        subtree = learn_decision_tree(subset, remaining_attributes, target_attr)
        tree[best_attr][value] = subtree
    return tree
def entropy(values: list) -> float:
    counter = Counter(values)
    total = len(values)
    return sum((-count / total * math.log2(count / total) for count in counter.values()))
def information_gain(examples: list[dict], attr: str, target_attr: str) -> float:
    total_entropy = entropy([example[target_attr] for example in examples])
    for (value, subset) in split_examples(examples, attr):
        subset_entropy = entropy([example[target_attr] for example in subset])
        total_entropy -= len(subset) / len(examples) * subset_entropy
    return total_entropy
def split_examples(examples: list[dict], attr: str) -> tuple[str, list[dict]]:
    return [(value, [example for example in examples if example[attr] == value]) for value in set([example[attr] for example in examples])]
def most_common(values: list) -> str:
    return Counter(values).most_common(1)[0][0]
