def linear_kernel(x, y):
    return np.dot(x, y)
def rbf_kernel(x, y, sigma):
    return np.exp(-np.linalg.norm(x - y) ** 2 / (2 * sigma ** 2))
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    bias = 0
    if kernel == 'linear':
        kernel_func = linear_kernel
    elif kernel == 'rbf':
        kernel_func = lambda x, y: rbf_kernel(x, y, sigma)
    for _ in range(iterations):
        for i in range(n_samples):
            value = 0
            for j in range(n_samples):
                value += alpha[j] * labels[j] * kernel_func(data[j], data[i])
            value += bias
            if labels[i] * value < 1:
                alpha[i] += 1
                bias += lambda_val * labels[i]
    sv_alpha_sum = 0
    for i in range(n_samples):
        sv_alpha_sum += alpha[i] * labels[i] * kernel_func(data[i], data[0])
    bias -= sv_alpha_sum
    return (alpha.tolist(), round(bias, 4))
