def rref(matrix):
    matrix = np.array(matrix, dtype=float)
    (r, c) = matrix.shape
    for i in range(min(r, c)):
        if matrix[i, i] == 0:
            for j in range(i + 1, r):
                if matrix[j, i] != 0:
                    matrix[[i, j]] = matrix[[j, i]]
                    break
        if matrix[i, i] != 1:
            matrix[i] = matrix[i] / matrix[i, i]
        for j in range(r):
            if j != i:
                matrix[j] = matrix[j] - matrix[i] * matrix[j, i]
    return matrix.tolist()
