def gauss_seidel(A, b, n, x_ini=None):
    L = np.tril(A)
    U = np.triu(A, 1)
    if x_ini is None:
        x = np.zeros_like(b)
    else:
        x = x_ini
    for _ in range(n):
        x = np.dot(np.linalg.inv(L), b - np.dot(U, x))
    return np.round(x.tolist(), 4)
