def compute_tf_idf(corpus, query):
    if len(corpus) == 0:
        raise ValueError('Corpus is empty')
    flattened_corpus = [' '.join(doc) for doc in corpus]
    vectorizer = TfidfVectorizer(smooth_idf=True)
    tfidf_matrix = vectorizer.fit_transform(flattened_corpus)
    idf_values = {term: vectorizer.idf_[i] for (term, i) in vectorizer.vocabulary_.items() if term in query}
    tf_idf_scores = []
    for doc in corpus:
        doc_tf_idf = []
        for term in query:
            if term in doc:
                tf = doc.count(term) / len(doc)
                idf = idf_values[term]
                tf_idf = tf * idf
            else:
                tf_idf = 0
            doc_tf_idf.append(tf_idf)
        tf_idf_scores.append(doc_tf_idf)
    return np.round(tf_idf_scores, 4).tolist()
