def calculate_brightness(img):
    if not img:
        return -1
    total_brightness = 0
    num_pixels = 0
    num_rows = len(img)
    num_cols = len(img[0])
    for row in img:
        if len(row) != num_cols:
            return -1
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
            total_brightness += pixel
            num_pixels += 1
    average_brightness = total_brightness / num_pixels
    return round(average_brightness, 2)
